mapper.select:
select count(*)
from scrap
where rvNo=#{rvNo} and nickName=#{nickName}

select count(*)
from scrap
where nickName=#{nickName}

SELECT *
FROM review 
WHERE 1=1

	AND RVCATEGORY1  = #{rvCategory1}


	AND RVCATEGORY2 = #{rvCategory2}	

ORDER BY rvNo DESC

mapper.update:
update review
set rvTitle=#{rvTitle}, rvCtt=#{rvCtt}
Where rvNo=#{rvNo}

mapper.select:
select count(*)
from rvLike
where rvNo=#{rvNo} and nickName=#{nickName}

mapper.update:
update review
set rvLikeCnt=(select count(*) from rvLike where rvNo=#{rvNo})
where rvNo=#{rvNo}
